import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000');

function getRandomColor() {
  const colors = ['#25D366', '#34B7F1', '#FFEB3B', '#FF5722', '#9C27B0'];
  return colors[Math.floor(Math.random() * colors.length)];
}

function Chat({ user, onLogout }) {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on('chat history', (history) => {
      setMessages(history);
    });
    socket.on('chat message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });
    return () => {
      socket.off('chat history');
      socket.off('chat message');
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && user) {
      socket.emit('chat message', {
        text: message,
        user: user.username,
        color: user.avatar ? undefined : getRandomColor(),
        avatar: user.avatar,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      });
      setMessage('');
    }
  };

  return (
    <div style={{ maxWidth: 480, margin: '2rem auto', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #0001', display: 'flex', flexDirection: 'column', height: '80vh' }}>
      <div style={{ background: '#075E54', color: '#fff', padding: '1rem', borderTopLeftRadius: 8, borderTopRightRadius: 8, fontWeight: 'bold', fontSize: 20, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 10 }}>
          {user.avatar ? (
            <img src={user.avatar} alt="avatar" style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', border: '2px solid #fff' }} />
          ) : (
            <div style={{ width: 36, height: 36, borderRadius: '50%', background: '#25D366', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: 16 }}>
              {user.username[0].toUpperCase()}
            </div>
          )}
          <span>{user.username}</span>
        </div>
        <button onClick={onLogout} style={{ background: '#fff', color: '#075E54', border: 'none', borderRadius: 4, padding: '4px 12px', fontWeight: 'bold', cursor: 'pointer' }}>Logout</button>
      </div>
      <div style={{ flex: 1, overflowY: 'auto', padding: 16, background: '#ece5dd' }}>
        {messages.map((msg, idx) => (
          <div key={msg._id || idx} style={{ display: 'flex', flexDirection: msg.user === user.username ? 'row-reverse' : 'row', alignItems: 'flex-end', marginBottom: 10 }}>
            {msg.avatar ? (
              <img src={msg.avatar} alt="avatar" style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', margin: '0 8px' }} />
            ) : (
              <div style={{ width: 36, height: 36, borderRadius: '50%', background: msg.color || '#25D366', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: 16, margin: '0 8px' }}>
                {msg.user ? msg.user[0].toUpperCase() : '?'}
              </div>
            )}
            <div style={{ maxWidth: '70%', background: msg.user === user.username ? '#dcf8c6' : '#fff', borderRadius: 8, padding: '8px 12px', boxShadow: '0 1px 2px #0001', position: 'relative' }}>
              <div style={{ fontWeight: 'bold', fontSize: 13, color: '#075E54' }}>{msg.user || 'Unknown'}</div>
              <div style={{ fontSize: 15 }}>{msg.text || msg}</div>
              <div style={{ fontSize: 11, color: '#888', textAlign: 'right', marginTop: 2 }}>{msg.time || ''}</div>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={sendMessage} style={{ display: 'flex', padding: 12, background: '#f0f0f0', borderBottomLeftRadius: 8, borderBottomRightRadius: 8 }}>
        <input
          type="text"
          value={message}
          onChange={e => setMessage(e.target.value)}
          placeholder="Type a message..."
          style={{ flex: 1, borderRadius: 20, border: '1px solid #ccc', padding: '10px 16px', marginRight: 8 }}
        />
        <button type="submit" style={{ background: '#25D366', color: '#fff', border: 'none', borderRadius: 20, padding: '0 20px', fontWeight: 'bold', fontSize: 16 }}>Send</button>
      </form>
    </div>
  );
}

export default Chat;
