import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000');

function getRandomColor() {
  const colors = ['#25D366', '#34B7F1', '#FFEB3B', '#FF5722', '#9C27B0'];
  return colors[Math.floor(Math.random() * colors.length)];
}

function Chat({ user, onLogout }) {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);

  // Fetch user list
  useEffect(() => {
    fetch(`/api/users?username=${encodeURIComponent(user.username)}`)
      .then(res => res.json())
      .then(setUsers);
  }, [user.username]);

  // Join own room for private messaging
  useEffect(() => {
    socket.emit('join', user.username);
  }, [user.username]);

  // Fetch private chat history when selectedUser changes
  useEffect(() => {
    if (!selectedUser) return;
    fetch(`/api/messages/${user.username}/${selectedUser.username}`)
      .then(res => res.json())
      .then(setMessages);
  }, [selectedUser, user.username]);

  // Listen for private messages
  useEffect(() => {
    const handler = (msg) => {
      if (
        (msg.from === user.username && msg.to === selectedUser?.username) ||
        (msg.from === selectedUser?.username && msg.to === user.username)
      ) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on('private message', handler);
    return () => socket.off('private message', handler);
  }, [selectedUser, user.username]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && user && selectedUser) {
      socket.emit('private message', {
        from: user.username,
        to: selectedUser.username,
        text: message,
        color: user.avatar ? undefined : getRandomColor(),
        avatar: user.avatar,
        time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
      });
      setMessage('');
    }
  };

  return (
    <div style={{ maxWidth: 900, margin: '2rem auto', background: '#fff', borderRadius: 8, boxShadow: '0 2px 8px #0001', display: 'flex', height: '80vh' }}>
      {/* Sidebar: User List */}
      <div style={{ width: 220, borderRight: '1px solid #eee', background: '#f7f7f7', borderTopLeftRadius: 8, borderBottomLeftRadius: 8, display: 'flex', flexDirection: 'column' }}>
        <div style={{ padding: 16, borderBottom: '1px solid #eee', background: '#075E54', color: '#fff', fontWeight: 'bold', borderTopLeftRadius: 8 }}>
          {user.avatar ? (
            <img src={user.avatar} alt="avatar" style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', border: '2px solid #fff', marginRight: 8 }} />
          ) : (
            <span style={{ width: 36, height: 36, borderRadius: '50%', background: '#25D366', color: '#fff', display: 'inline-flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: 16, marginRight: 8 }}>{user.username[0].toUpperCase()}</span>
          )}
          {user.username}
        </div>
        <div style={{ flex: 1, overflowY: 'auto' }}>
          {users.map(u => (
            <div key={u.username} onClick={() => setSelectedUser(u)} style={{ cursor: 'pointer', padding: 12, background: selectedUser?.username === u.username ? '#e0f2f1' : 'transparent', display: 'flex', alignItems: 'center', gap: 10 }}>
              {u.avatar ? (
                <img src={u.avatar} alt="avatar" style={{ width: 32, height: 32, borderRadius: '50%', objectFit: 'cover' }} />
              ) : (
                <span style={{ width: 32, height: 32, borderRadius: '50%', background: '#34B7F1', color: '#fff', display: 'inline-flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: 15 }}>{u.username[0].toUpperCase()}</span>
              )}
              <span>{u.username}</span>
            </div>
          ))}
        </div>
        <button onClick={onLogout} style={{ margin: 16, background: '#fff', color: '#075E54', border: '1px solid #075E54', borderRadius: 4, padding: '6px 0', fontWeight: 'bold', cursor: 'pointer' }}>Logout</button>
      </div>
      {/* Main Chat Area */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <div style={{ background: '#075E54', color: '#fff', padding: '1rem', borderTopRightRadius: 8, fontWeight: 'bold', fontSize: 20, minHeight: 60, display: 'flex', alignItems: 'center' }}>
          {selectedUser ? (
            <>
              {selectedUser.avatar ? (
                <img src={selectedUser.avatar} alt="avatar" style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', border: '2px solid #fff', marginRight: 8 }} />
              ) : (
                <span style={{ width: 36, height: 36, borderRadius: '50%', background: '#34B7F1', color: '#fff', display: 'inline-flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: 16, marginRight: 8 }}>{selectedUser.username[0].toUpperCase()}</span>
              )}
              {selectedUser.username}
            </>
          ) : 'Select a user to chat'}
        </div>
        <div style={{ flex: 1, overflowY: 'auto', padding: 16, background: '#ece5dd' }}>
          {selectedUser ? (
            messages.map((msg, idx) => (
              <div key={msg._id || idx} style={{ display: 'flex', flexDirection: msg.from === user.username ? 'row-reverse' : 'row', alignItems: 'flex-end', marginBottom: 10 }}>
                {msg.avatar ? (
                  <img src={msg.avatar} alt="avatar" style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', margin: '0 8px' }} />
                ) : (
                  <div style={{ width: 36, height: 36, borderRadius: '50%', background: msg.color || '#25D366', color: '#fff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', fontSize: 16, margin: '0 8px' }}>
                    {msg.from ? msg.from[0].toUpperCase() : '?'}
                  </div>
                )}
                <div style={{ maxWidth: '70%', background: msg.from === user.username ? '#dcf8c6' : '#fff', borderRadius: 8, padding: '8px 12px', boxShadow: '0 1px 2px #0001', position: 'relative' }}>
                  <div style={{ fontWeight: 'bold', fontSize: 13, color: '#075E54' }}>{msg.from || 'Unknown'}</div>
                  <div style={{ fontSize: 15 }}>{msg.text || msg}</div>
                  <div style={{ fontSize: 11, color: '#888', textAlign: 'right', marginTop: 2 }}>{msg.time || ''}</div>
                </div>
              </div>
            ))
          ) : (
            <div style={{ color: '#888', textAlign: 'center', marginTop: 40 }}>Select a user to start chatting</div>
          )}
          <div ref={messagesEndRef} />
        </div>
        {selectedUser && (
          <form onSubmit={sendMessage} style={{ display: 'flex', padding: 12, background: '#f0f0f0', borderBottomRightRadius: 8 }}>
            <input
              type="text"
              value={message}
              onChange={e => setMessage(e.target.value)}
              placeholder={`Message @${selectedUser.username}`}
              style={{ flex: 1, borderRadius: 20, border: '1px solid #ccc', padding: '10px 16px', marginRight: 8 }}
            />
            <button type="submit" style={{ background: '#25D366', color: '#fff', border: 'none', borderRadius: 20, padding: '0 20px', fontWeight: 'bold', fontSize: 16 }}>Send</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default Chat;
